import React, { useState, useEffect } from 'react';


const MenuSection = () => {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [imageIndex, setImageIndex] = useState({});

  useEffect(() => {
    let interval;
    if (hoveredItem !== null) {
      interval = setInterval(() => {
        setImageIndex((prev) => ({
          ...prev,
          [hoveredItem]: (prev[hoveredItem] + 1) % menuItems.find(item => item.id === hoveredItem).images.length
        }));
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [hoveredItem]);

  const getCurrentImage = (item) => {
    return item.images[imageIndex[item.id] || 0];
  };

  const menuItems = [
  {
    id: 1,
    name: "Butter Chicken",
    description: "Creamy tomato-based curry with tender chicken pieces, served with basmati rice",
    price: "₹320",
    images: [
      "https://images.unsplash.com/photo-1603894584373-5ac82b2ae398?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=600",
      "data:image/webp;base64,",
      "https://th.bing.com/th/id/OIP.tzG0D-E7C0rdoaIXJPdA2QHaHa?w=202&h=269&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3"
    ]
  },
  {
    id: 2,
    name: "Paneer Makhani",
    description: "Rich cottage cheese curry in creamy tomato sauce with aromatic spices",
    price: "₹280",
    images: [
      "https://images.unsplash.com/photo-1606491956689-2ea866880c84?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=600",
      "https://th.bing.com/th/id/OIP.DQJzkIFKyluxTtavKce8nAHaHh?w=196&h=200&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3",
      "data:image/webp;base64,"
    ]
  },
  {
    id: 3,
    name: "Gulab Jamun",
    description: "Traditional Indian sweet dumplings soaked in rose-flavored sugar syrup",
    price: "₹120",
    images: [
      "https://1.bp.blogspot.com/-YGBSprsgs1c/XdDVy-pGurI/AAAAAAAAX64/uN_6FF2b6BIYgI2tzz3DXlWNvdlY_jZ_QCLcBGAsYHQ/s1600/gulab%2Bjamun%2B9.JPG",
      "data:image/webp;base64,",
      "data:image/webp;base64,"
    ]
  }
];



  return (
    <section id="menu" className="py-20 bg-restaurant-light">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="font-serif text-4xl md:text-5xl font-bold text-restaurant-dark mb-4">
            हमारा खास मेन्यू (Our Special Menu)
          </h2>
          <p className="text-restaurant-text-secondary text-lg max-w-2xl mx-auto font-medium">
            Each dish is lovingly prepared by Aunty using traditional family recipes and the finest spices
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {menuItems.map((item) => (
            <div
              key={item.id}
              className="bg-white rounded-xl shadow-lg overflow-hidden transform hover:scale-105 transition-transform-smooth cursor-spice"
            >
              <div
                className="relative group"
                onMouseEnter={() => {
                  setHoveredItem(item.id);
                  setImageIndex((prev) => ({ ...prev, [item.id]: 0 }));
                }}
                onMouseLeave={() => {
                  setHoveredItem(null);
                  setImageIndex((prev) => ({ ...prev, [item.id]: 0 }));
                }}
              >
                <img
                  src={getCurrentImage(item)}
                  alt={item.name}
                  className="w-full h-48 object-cover transition-opacity duration-500"
                  loading="lazy"
                />

                <div className="absolute inset-0 bg-black bg-opacity-10 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
  <span className="text-white text-sm font-medium bg-black bg-opacity-40 px-2 py-0.5 rounded">
    👀 Hover to preview
  </span>
</div>
                <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 flex space-x-1">
                  {item.images.map((_, idx) => (
                    <span
                      key={idx}
                      className={`w-2 h-2 rounded-full ${
                        (imageIndex[item.id] || 0) === idx
                          ? 'bg-white'
                          : 'bg-gray-300'
                      }`}
                    ></span>
                  ))}
                </div>
              </div>

              <div className="p-6">
                <h3 className="font-serif text-xl font-semibold text-restaurant-dark mb-2">
                  {item.name}
                </h3>
                <p className="text-restaurant-text-secondary mb-4 font-medium">
                  {item.description}
                </p>
                <div className="flex justify-between items-center">
                  <span className="font-serif text-2xl font-bold text-restaurant-accent">
                    {item.price}
                  </span>
                  <button className="bg-restaurant-primary hover:bg-restaurant-accent text-white px-4 py-2 rounded-lg transition-all-smooth transform hover:scale-105">
                    🛒 Add to Order
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-12">
          <button className="bg-restaurant-accent hover:bg-restaurant-primary text-white px-8 py-4 rounded-full text-lg font-semibold transition-transform-smooth transform hover:scale-105 cursor-spice shadow-lg hover:shadow-xl">
            📜 View Full Menu
          </button>
        </div>
      </div>
    </section>
  );
};

export default MenuSection;